import React, {useState, useEffect} from 'react';
import axios from 'axios';

export default function Stats({teamId, season}){
  const teamInfoUrl = 'http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=' + season;
  // const teamBackgroundColor = 'white';
  // const teamTextColor = '#33006F';
  // const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    let cancel
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    }).then(res => {
      setLoading(false);
      console.log(res.data.records);
      var t;
      for(var i = 0; i < res.data.records.length; i++){
        for(var j = 0; j<res.data.records[i].teamRecords.length; j++){
          if(res.data.records[i].teamRecords[j].id === teamId){
            t = res.data.records[i].teamRecords[j];
            console.log("found team");
          }
        }
      }
      if (t) {console.log(t);}
    })

    return () => cancel()
  }, [season, teamId])

  if(loading) return "Loading...";
  return(
    <div>

    </div>
  );
}
