//"http://statsapi.mlb.com/api/v1/stats?stats=season&teamId=113&group=hitting&season=1999"

import React, {useState, useEffect} from 'react';
import StatBlock from './StatBlock';
import axios from 'axios';

export default function TopPlayers({theme, season, teamId, group}){
  // console.log(stats);
  const [top3, setTop3] = useState([{"name":"-","stat":"-"}, {"name":"-","stat":"-"}, {"name":"-","stat":"-"}]);
  const style = {
    "backgroundColor":theme.accentColor,
    "color": "#ffffff"
  }
  const mainStat = useState({
    "hitting":"avg",
    "pitching":"era",
    "fielding":"fielding"
  });

  useEffect(() => {
    let cancel;
    axios.get("http://statsapi.mlb.com/api/v1/stats?stats=season&teamId="+ teamId +"&group="+ group +"&season="+ season, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      setTop3([
        {
        "name":res.data.stats.splits[0].player.name,
        "stat":res.data.stats.splits[0].stat[mainStat[group]]
        },
        {
        "name":res.data.stats.splits[0].player.name,
        "stat":res.data.stats.splits[0].stat[mainStat[group]]
        },
        {
        "name":res.data.stats.splits[0].player.name,
        "stat":res.data.stats.splits[0].stat[mainStat[group]]
        }
      ]);
    })
    .catch(err =>{

    })
    return () => cancel()
  }, [season, teamId,])

  return(
    <div className="stat-list">
      <div class="player-row">
        <h3>{group.charAt(0).toUpperCase() + group.slice(1)}</h3>
        <h3>{mainStat[group]}</h3>
      </ div>
      <ol className="top-3">
        {top3.map(player => (
        <li key={player.name} class="player-row"> <h3>{player.name}</h3> <h3>{player.stat}</h3> </li>))
        }
      </ol>
    </div>
  );
}
