import React from 'react';
import TeamColors from './TeamColors'
export default function TeamHeader({ loading, teamName, season }){

  const style = {
    "backgroundColor":"#231F20",
    "color": "#C4CED4"
  }
  if(!loading) return(
    <header style={style}>
      <h1>{teamName}</h1>
      <h1>{season}</h1>
    </header>
  );
  else{
    style["justifyContent"] = "center";
    return(
    <header style={style}>
      <h1>Loading</h1>
    </header>
    );
  }
}
