import axios from 'axios';

export default function Stats({teamId, season}){
  const teamInfoUrl = 'http://statsapi.mlb.com/api/v1/teams?teamId='+ teamId +'&season='+ season;
  const teamBackgroundColor = 'white';
  const teamTextColor = '#33006F';
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    let cancel
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    }).then(res => {
      setLoading(false);
    })

    return () => cancel()
  }, [year, teamId])

  if(loading) return "Loading...";
