import React, {useContext} from 'react';
import TeamColors from './TeamColors'
export default function TeamHeader({ theme, loading, teamName, season, teamFile}){
  const style = {
    "backgroundColor":theme.darkColor,
    "color": "#ffffff",
    "borderColor": theme.accentColor
  }
  return(
    <header style={style}>
      <div>
        <img alt={teamName+" Logo"} src={"https://clientcontent.mlb.com/images/wallpapers/images/teams/large/"+ teamFile +"_wallpaper_logo_01.png"}/>
        <h1>{teamName}</h1>
      </div>
      <h1>{season}</h1>
    </header>
  );
}
