import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import App from './App';
import './index.css';


class SerialCommunicationApp extends React.Component {
          constructor(props) {
            super(props);
            this.state = { year: 2019 };
          }

          componentDidMount() {
          this.interval = setInterval(() => {
            let cancelYear
            axios.get("http://localhost:5000/api/data", {
              cancelToken: new axios.CancelToken(c => cancelYear = c),
              timeout:2000
            })
            .then(res => {
              this.setState({
                year: res.data.express
              });
            })
            .catch(err =>{
              console.log(err.message);
            })

            return () => cancelYear()
          }, 1000);
          }
          componentWillUnmount() {
            clearInterval(this.interval);
          }

          render() {
            return (
              <App year={parseInt(this.state.year)}/>
            );
          }
        }

ReactDOM.render(
  <SerialCommunicationApp />, document.getElementById('root')
);
