  const teamInfoUrl = 'http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=' + season;
  // const teamBackgroundColor = 'white';
  // const teamTextColor = '#33006F';
  // const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    let cancel
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    }).then(res => {
      setLoading(false);
      console.log(res.data);
      // var t;
      // for(var i; i < res.data.results.length; i++){
      //   for(var j; j<res.data.results[i].teamRecords.length; j++){
      //     if(res.data.results[i].teamRecords[j].id === teamId){
      //       t = res.data.results[i].teamRecords[j];
      //       console.log("found team");
      //     }
      //   }
      // }
      // if (t) {console.log(t);}
    })

    return () => cancel()
  }, [season, teamId])

  if(loading) return "Loading...";
  return(
    <div>

    </div>
  );
}
