import React, {useState, useEffect} from 'react';
import axios from 'axios';
import TeamHeader from './TeamHeader';
import Stats from './Stats';

function App(){
  const [year, setYear] = useState(2019);
  const [teamId, setTeamId] = useState(115);
  const [error, setError] = useState(false);
  const [teamFound, setTeamFound] = useState(false);
  const [teamName, setTeamName] = useState("");
  const [loading, setLoading] = useState(true);
  const [theme, setTheme] = useState({
    "accentColor": "#333366",
    "darkColor": "#231F20",
    "lightColor": "#C4CED4"
  });
  // const [teamStats, setTeamStats] = useState({});
  const [teamInfoUrl, setTeamInfoUrl] = useState('http://statsapi.mlb.com/api/v1/teams?teamId='+ teamId +'&season='+ year);
  // const [teamInfoUrl, setTeamInfoUrl] = useState('http://statsapi.mlb.com/api/v1/teams?teamId='+ teamId +'&season='+ year);
  // const teamInfoUrl = 'http://statsapi.mlb.com/api/v1/teams?teamId='+ teamId +'&season='+ year;
  // const teamBackgroundColor = '#33006F';
  // const teamTextColor = '#C4CED4';

  useEffect(() => {
    const interval = setInterval(() => {
      let cancelYear
      axios.get("http://localhost:5000/api/data", {
        cancelToken: new axios.CancelToken(c => cancelYear = c),
        timeout:100
      })
      .then(res => {
        setYear(res.data.year);
        setTeamId(res.data.id);
        setTheme(res.data.theme);
      })
      .catch(err =>{
        console.log(err.message);
      });
      return () => cancelYear()
    }, 100);
  },[])

  useEffect(() => {
    setError(false);
    setLoading(true);
    setTeamFound(false);
    setTeamInfoUrl('http://statsapi.mlb.com/api/v1/teams?teamId='+ teamId +'&season='+ year);
    let cancel
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c),
      timeout:2000
    })
    .then(res => {
      setTeamName(res.data.teams[0].name);
      setLoading(false);
      setTeamFound(true);
    })
    .catch(err =>{
      setLoading(false);
      setTeamFound(false);
      setError(true);
      // console.log(year);
      console.log(err.message);
      // return "team not found";
    })

    return () => cancel()
  }, [year, teamId])

  // if(loading) return "loading..."

  return(
    <div>
      <TeamHeader theme={theme} teamName={teamName} season={year}/>
      <Stats theme={theme} error={error} id={teamId} year={year} teamInfoUrl={"http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=" + year}/>
    </div>
  );

}

export default App;
