import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import './index.css';


class serialApplication extends React.Component {
          componentDidMount() {
          this.interval = setInterval(() => {
            let cancelYear
            axios.get(teamInfoUrl, {
              cancelToken: new axios.CancelToken(c => cancelYear = c),
              timeout:200
            })
            .then(res => {
              setTeamName(res.data.teams[0].name);
              setLoading(false);
              setTeamFound(true);
            })
            .catch(err =>{
              setLoading(false);
              setTeamFound(false);
              console.log(err.message);
            })

            return () => cancelYear()
          }, 100);
          }
          componentWillUnmount() {
            clearInterval(this.interval);
          }

          render() {
            return (
              <App />
            );
          }
        }

ReactDOM.render(
  <serialApplication />, document.getElementById('root')
);
