import React, {useState, useEffect} from 'react';
import StatBlock from './StatBlock';
import SeasonRecord from './SeasonRecord';
import axios from 'axios';

export default function Stats({id, year}){
  const [teamId, setId] = useState(id);
  const [season, setSeason] = useState(year);
  const [teamInfoUrl, setTeamInfoUrl] = useState("");
  // const teamBackgroundColor = 'white';
  // const teamTextColor = '#33006F';
  const [stats, setStats] = useState({
    "wins":"",
    "losses":"",
    "winningPercentage":"",
    "leagueGamesBack":"",
    "records":{
      "splitRecords":[{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""}]
    }
  });
  const [record, setRecord] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    setId(id);
    setSeason(year);
    setTeamInfoUrl("http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=" + year)
    let cancel;
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      setLoading(false);
      console.log(res.data.records);
      var t;
      for(var i = 0; i < res.data.records.length; i++){
        for(var j = 0; j<res.data.records[i].teamRecords.length; j++){
          if(res.data.records[i].teamRecords[j].team.id === teamId){
            setStats(res.data.records[i].teamRecords[j]);
          }
        }
      }
    })
    .catch(err =>{
      setLoading(false);
      // console.log(err.message);
      return err.message;
    })

    return () => cancel()
  }, [year, id, stats])


  // const stats_being_displayed = [{'display': 'Games Played', 'key': 'gamesPlayed'}, {'display': 'Wins', 'key': 'wins'}, {'display': 'Losses', 'key': 'losses'}, {'display': 'Win %', 'key': 'winningPercentage'}, {'display': 'Runs Allowed', 'key': 'runsAllowed'}, {'display': 'Runs Scored', 'key': 'runsScored'}, {'display': 'Division Rank', 'key': 'divisionRank'}, {'display': 'League Rank', 'key': 'leagueRank'}, {'display': 'Sport Rank', 'key': 'sportRank'}]

  return(
    <div className="stats-container">
      <SeasonRecord stats={stats}/>
    </div>
  );
}
