import React, {useState, useEffect} from 'react';
import StatBlock from './StatBlock';
import axios from 'axios';

export default function Stats({teamId, season}){
  const [teamInfoUrl] = useState('http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=' + season);
  // const teamBackgroundColor = 'white';
  // const teamTextColor = '#33006F';
  const [error, setError] = useState([false, ""]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    let cancel;
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      setLoading(false);
      console.log(res.data.records);
      var t;
      for(var i = 0; i < res.data.records.length; i++){
        for(var j = 0; j<res.data.records[i].teamRecords.length; j++){
          if(res.data.records[i].teamRecords[j].team.id === teamId){
            setStats(res.data.records[i].teamRecords[j]);
          }
        }
      }
    })
    .catch(err =>{
      setLoading(false);
      console.log(err.message);
    })

    return () => cancel()
  }, [season, teamId])

  if(loading) return "Loading...";

  const stats_being_displayed = [{'display': 'Games Played', 'key': 'gamesPlayed'}, {'display': 'Wins', 'key': 'wins'}, {'display': 'Losses', 'key': 'losses'}, {'display': 'Winning Percentage', 'key': 'winningPercentage'}, {'display': 'Runs Allowed', 'key': 'runsAllowed'}, {'display': 'Runs Scored', 'key': 'runsScored'}, {'display': 'Division Rank', 'key': 'divisionRank'}, {'display': 'League Rank', 'key': 'leagueRank'}, {'display': 'Sport Rank', 'key': 'sportRank'}]

  return(
    <div className="stats-container">
    {stats_being_displayed.map(s => (
      <StatBlock key={s.key} title={s.display} statistic={stats[s.key]}/>
    ))}
    </div>
  );
}
