import React, {useState, useEffect} from 'react';
import StatBlock from './StatBlock';
import SeasonRecord from './SeasonRecord';
import axios from 'axios';

export default function Stats({id, year, teamInfoUrl}){
  // const [teamInfoUrl, setTeamInfoUrl] = useState("http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=" + year);
  // console.log(id +" " + year);
  const [stats, setStats] = useState({
    "wins":"",
    "losses":"",
    "winningPercentage":"",
    "leagueGamesBack":"",
    "records":{
      "splitRecords":[{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""}]
    }
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    console.log(teamInfoUrl);
    let cancel;
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      setLoading(false);
      // console.log(teamInfoUrl);
      console.log(res.data.records);
      for(var i = 0; i < res.data.records.length; i++){
        for(var j = 0; j<res.data.records[i].teamRecords.length; j++){
          console.log(res.data.records[i].teamRecords[j].team.id);
          if(res.data.records[i].teamRecords[j].team.id === id){
            setStats(res.data.records[i].teamRecords[j]);
          }
        }
      }
    })
    .catch(err =>{
      setLoading(false);
      // console.log(err.message);
      return err.message;
    })
    return () => cancel()
  }, [year, id])
  // console.log(stats);
  // const stats_being_displayed = [{'display': 'Games Played', 'key': 'gamesPlayed'}, {'display': 'Wins', 'key': 'wins'}, {'display': 'Losses', 'key': 'losses'}, {'display': 'Win %', 'key': 'winningPercentage'}, {'display': 'Runs Allowed', 'key': 'runsAllowed'}, {'display': 'Runs Scored', 'key': 'runsScored'}, {'display': 'Division Rank', 'key': 'divisionRank'}, {'display': 'League Rank', 'key': 'leagueRank'}, {'display': 'Sport Rank', 'key': 'sportRank'}]
  if(loading) return 0;
  return(
    <div className="stats-container">
    <section className="stat-block" id="record">
      <h2>Season Record</h2>
      <table>
        <thead>
          <tr>
            <th>Win</th><th>Lose</th><th>Win%</th><th>GB</th><th>Home</th><th>Away</th><th>L10</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className="small-col">{stats.wins}</td>
            <td>{stats.losses}</td>
            <td>{stats.winningPercentage}</td>
            <td>{stats.leagueGamesBack}</td>
            <td>{stats.records.splitRecords[0].wins} - {stats.records.splitRecords[0].losses}</td>
            <td>{stats.records.splitRecords[1].wins} - {stats.records.splitRecords[1].losses}</td>
            <td>{stats.records.splitRecords[4].wins} - {stats.records.splitRecords[4].losses}</td>
          </tr>
        </tbody>
      </table>
    </section>
    </div>
  );
}
