import React, {useState, useEffect} from 'react';
import axios from 'axios';
import TeamHeader from './TeamHeader';

function App(){
  const [year, setYear] = useState(2019);
  const [teamId, setTeamId] = useState(114);
  const [teamName, setTeamName] = useState();
  const [teamStats, setTeamStats] = useState({});
  const [seasonStandingsUrl, setSeasonStandingsUrl] = useState('http://statsapi.mlb.com/api/v1/standings/regularSeason?leagueId=103,104&season=' + year);
  const [teamInfoUrl, setTeamInfoUrl] = useState('http://statsapi.mlb.com/api/v1/teams?teamId='+ teamId +'&season='+ year);


  useEffect(() => {
    let cancel
    axios.get(teamInfoUrl, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    }).then(res => {
      setTeamName(res.data.teams[0].name);

      // setLoading(false)
      // setNextPageUrl(res.data.next)
      // setPrevPageUrl(res.data.previous)
      // setPokemon(res.data.results.map(p => p.name))
    })

    return () => cancel()
  }, [year, teamId])

  return(
    <TeamHeader teamId={teamName}/>
  );
}

export default App;
