//"http://statsapi.mlb.com/api/v1/stats?stats=season&teamId=113&group=hitting&season=1999"

import React, {useState, useEffect} from 'react';
import axios from 'axios';

export default function AboutTeam({theme, teamId, season, teamInfo}){
  // console.log(stats);

  const [manager,setManager] = useState("-");
//   const [manager,setManager] = useState("-");


  const style = {
    "backgroundColor":theme.accentColor,
    "color": "#ffffff"
  }

  useEffect(() => {
    let cancel;
    axios.get("http://statsapi.mlb.com/api/v1/teams/"+ teamId + "/coaches?season="+ season, {
    cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
        setManager(res.data.roster[0].person.fullName);
    })
    .catch(err =>{

    })
    return () => cancel()
  }, [teamInfo])
  // console.log(top3);

  return(
    <div className="stat-block" id="about-team" style={style}>
        <div className="runs-row">
            <h3>League</h3> <h4>{teamInfo.league.name}</h4>
        </div>
        <div className="runs-row">
            <h3>Year Est.</h3> <h4>{teamInfo.firstYearOfPlay}</h4>
        </div>
        <div className="runs-row">
            <h3>Venue</h3><h4>{teamInfo.venue.name}</h4>
        </div>
        <div className="runs-row">
            <h3>Location</h3> <h4>{teamInfo.locationName}</h4>
        </div>
    </div>
  );
}
