//"http://statsapi.mlb.com/api/v1/stats?stats=season&teamId=113&group=hitting&season=1999"

import React, {useState, useEffect} from 'react';
import StatBlock from './StatBlock';
import axios from 'axios';

export default function TopPlayers({theme, season, teamId}){
  // console.log(stats);
  const style = {
    "backgroundColor":theme.accentColor,
    "color": "#ffffff"
  }

  useEffect(() => {
    setLoading(true);
    // console.log(teamInfoUrl);
    let cancel;
    axios.get("http://statsapi.mlb.com/api/v1/stats?stats=season&teamId="+ teamId +"&group="+ group +"&season="+ season, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      setLoading(false);
      // console.log(teamInfoUrl);
      // console.log(res.data.records);
      for(var i = 0; i < res.data.records.length; i++){
        for(var j = 0; j<res.data.records[i].teamRecords.length; j++){
          // console.log(res.data.records[i].teamRecords[j].team.id + ": ");
          // console.log(typeof res.data.records[i].teamRecords[j].team.id);
          // console.log(typeof id);
          if(res.data.records[i].teamRecords[j].team.id === parseInt(id)){
            console.log("match");
            setStats(res.data.records[i].teamRecords[j]);
          }
        }
      }
    })
    .catch(err =>{
      setLoading(false);
      setStats({
        "wins":"-",
        "losses":"-",
        "winningPercentage":"-",
        "leagueGamesBack":"-",
        "records":{
          "splitRecords":[{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""},{"wins":"","losses":""}]
        }
      });
      // console.log(err.message);
      // return err.message;
    })
    return () => cancel()
  }, [year, id,])

  return(
    <section className="stat-block" id="record" style={style}>
      <h2>Season Leaders</h2>
      <div>
        <h3>Hitting</h3>

      </div>
      <div>
        <h3>Pitching</h3>
      </div>
      <div>
        <h3>Fielding</h3>
      </div>
    </section>
  );
}
