//"http://statsapi.mlb.com/api/v1/stats?stats=season&teamId=113&group=hitting&season=1999"

import React, {useState, useEffect} from 'react';
import StatBlock from './StatBlock';
import axios from 'axios';

export default function PostseasonResults({theme, season, teamId, group}){
  // console.log(stats);

  const style = {
    "backgroundColor":theme.accentColor,
    "color": "#ffffff"
  }

  useEffect(() => {
    let cancel;
    axios.get("http://statsapi.mlb.com/api/v1/stats?stats=season&teamId="+ teamId +"&group="+ group +"&season="+ season, {
      cancelToken: new axios.CancelToken(c => cancel = c)
    })
    .then(res => {
      console.log(res.data.stats[0].splits[0].player);
      if(group != "pitching"){
        setTop3([
          {
          "name":res.data.stats[0].splits[0].player.fullName,
          "stat":res.data.stats[0].splits[0].stat[mainStat[group][0]]
          },
          {
          "name":res.data.stats[0].splits[1].player.fullName,
          "stat":res.data.stats[0].splits[1].stat[mainStat[group][0]]
          },
          {
          "name":res.data.stats[0].splits[2].player.fullName,
          "stat":res.data.stats[0].splits[2].stat[mainStat[group][0]]
          }
        ]);
      } else{
        setTop3([
          {
          "name":res.data.stats[0].splits[0].player.fullName,
          "stat":res.data.stats[0].splits[0].stat[mainStat[group][0]]
          }
        ]);
      }
      console.log(group + " is working");
    })
    .catch(err =>{
      console.log(group + " is not working");
    })
    return () => cancel()
    console.log(top3);
  }, [season, teamId])
  // console.log(top3);

  return(
    <div className="stat-list" style={style}>
      <div className="top-player-container">
        <h3>{group.charAt(0).toUpperCase() + group.slice(1)+ ' - (' + mainStat[group][1]+")"}</h3>
      </ div>
      <ol className="top-3">
        {top3.map(player => (
        <li key={player}> 
          <div className="player-row">
            <h4 className="player-name">{player.name}</h4> 
            <h4 className="player-stat">{player.stat}</h4> 
          </div>
        </li>))
        }
      </ol>
    </div>
  );
}
